%Script_trabajo_especialclearclcepsilon = 0.1;fprintf('EPSILON %f\n\n',epsilon);[probabilidad,todas_las_probabilidades] = calcular_probabilidad(epsilon);fprintf('La probabilidad de que falle dos veces seguidas es de: %d\n', probabilidad);% Grafico de la evolucion de la probabilidad a traves de las iteracionesfigure,plot(todas_las_probabilidades);hold onplot(todas_las_probabilidades);%plot(ones(size(todas_las_probabilidades)) * 0.5, '--');xlabel('Iteracion');ylabel('Probabilidad');ylim([0 1]);legend('Evolucion probabilidad', 'Probabilidad analitica');probIni=std(todas_las_probabilidades(1:20));fprintf('El desvio estandar de las probabilidades parciales de las primeras 20 iteraciones: %f\n', probIni);probFin=std(todas_las_probabilidades(end-20:end));fprintf('El desvio estandar de las probabilidades parciales de las ultimas 20 iteraciones: %f\n', probFin);fprintf('\n\n\n');%Y = std(todas_las_probabilidades (:))epsilon = 0.01;[probabilidad1,todas_las_probabilidades1] = calcular_probabilidad(epsilon);fprintf('EPSILON %f\n\n',epsilon);fprintf('La probabilidad de que falle dos veces seguidas es de: %d\n', probabilidad1);% Grafico de la evolucion de la probabilidad a traves de las iteracionesfigure,plot(todas_las_probabilidades1);hold onplot(todas_las_probabilidades1);%plot(ones(size(todas_las_probabilidades)) * 0.5, '--');xlabel('Iteracion');ylabel('Probabilidad');ylim([0 1]);legend('Evolucion probabilidad', 'Probabilidad analitica');probIni1=std(todas_las_probabilidades1(1:20));fprintf('El desvio estandar de las probabilidades parciales de las primeras 20 iteraciones: %f\n', probIni1);probFin1=std(todas_las_probabilidades1(end-20:end));fprintf('El desvio estandar de las probabilidades parciales de las ultimas 20 iteraciones: %f\n', probFin1);fprintf('\n\n\n');epsilon = 0.001;fprintf('EPSILON %f\n\n',epsilon);[probabilidad,todas_las_probabilidades] = calcular_probabilidad(epsilon);fprintf('La probabilidad de que falle dos veces seguidas es de: %d\n', probabilidad);% Grafico de la evolucion de la probabilidad a traves de las iteracionesfigure,plot(todas_las_probabilidades);hold onplot(todas_las_probabilidades);%plot(ones(size(todas_las_probabilidades)) * 0.5, '--');xlabel('Iteracion');ylabel('Probabilidad');ylim([0 1]);legend('Evolucion probabilidad', 'Probabilidad analitica');probIni2=std(todas_las_probabilidades(1:20));fprintf('El desvio estandar de las probabilidades parciales de las primeras 20 iteraciones: %f\n', probIni2);probFin2=std(todas_las_probabilidades(end-20:end));fprintf('El desvio estandar de las probabilidades parciales de las ultimas 20 iteraciones: %f\n', probFin2);fprintf('\n\n\n');